import React, { useEffect } from 'react';
import { getClicks } from '../blockchain/web3/web3client';
import { connectWallet } from '../blockchain/web3/web3client';

type Props = {};

export default function Counter({}: Props) {
  const [clicks, setClicks] = React.useState(0)
  const [wallets, setWallets] = React.useState<string[]>([])

  useEffect(() => {
    getClicks().then(res => {
      setClicks(res);
    });
    connectWallet().then(res =>{
      setWallets(res);
    })
  }, []);

  return (<>
    <div>
      <button onClick={() => getClicks()}>Click</button> 
      <br/>
      {clicks}
      <br/>
      <button>Reset</button>
      {/* <button>Connect to Wallte</button></> */}
      <ul>
        {wallets.map(w=>{
          <li>{w}</li>
        })}
      </ul>
    </div>
    </>
  );
}
